INSERT INTO book (book_id, isbn, title, subtitle, published, publisher, pages, description, inStock) VALUES (1, '978-1617291999', 'Java 8 in Action', 'Lambdas, Streams, and functional-style programming', '2014-08-28', 'Manning Publications', 424, 'Java 8 in Action is a clearly written guide to the new features of Java 8. The book covers lambdas, streams, and functional-style programming. With Java 8s functional features you can now write more concise code in less time, and also automatically benefit from multicore architectures. Its time to dig in!', 1);
INSERT INTO book (book_id, isbn, title, subtitle, published, publisher, pages, description, inStock) VALUES (2, '978-0134494166', 'Clean Architecture', 'A Craftsmans Guide to Software Structure and Design', '2017-09-20', 'Prentice Hall', 432, 'By applying universal rules of software architecture, you can dramatically improve developer productivity throughout the life of any software system. Now, building upon the success of his best-selling books Clean Code and The Clean Coder, legendary software craftsman Robert C. Martin (\"Uncle Bob\") reveals those rules and helps you apply them.', 1);
INSERT INTO book (book_id, isbn, title, subtitle, published, publisher, pages, description, inStock) VALUES (3, '978-1617292231', 'Grokking Algorithms', 'An illustrated guide for programmers and other curious people', '2016-05-01', 'Manning Publications', 256, 'Grokking Algorithms is a fully illustrated, friendly guide that teaches you how to apply common algorithms to the practical problems you face every day as a programmer. Youll start with sorting and searching and, as you build up your skills in thinking algorithmically, youll tackle more complex concerns such as data compression and artificial intelligence. Each carefully presented example includes helpful diagrams and fully annotated code samples in Python.', 1);
